import React, { useState } from 'react';

// import Counter from './Counter';

function App() {
  const [name, setName] = useState("");
  const [interest, setInterest] = useState("");
  const [tnc, setTnc] = useState(false);
  function getSubmitData(e){
    e.preventDefault();
    //console.log(name,interest,tnc);
    // if(interest === "Select"){
    //   console.log("please select any Option");
    // }
  }

  return(
    <center>
      <br /><br />
      <form onSubmit={getSubmitData}>
        <input type='text' value={name?name:''} onChange={(e)=> setName(e.target.value)}/> <br /><br />
        <select onChange={(e)=> setInterest(e.target.value)}>
          <option>Select</option>
          <option>One</option>
          <option>Two</option>
        </select><br /><br />
        <input type="checkbox" onChange={()=>setTnc(true)}/>Accept Here <br /><br />
        <button type='submit'>Submit</button>
        <button onClick={(e)=> getSubmitData()}>Clear</button>
      </form>
    </center>
  );
}
export default App;
